mapper.select:
/* item.selectItemGrpList */
SELECT	(
		SELECT	COUNT(1)
		FROM	T_CO_USER
		WHERE USER_ID = #{userId}
		)
		+
		(
		SELECT	COUNT(1)
		FROM	T_CO_CUST_USER
		WHERE	USER_ID = #{userId}
		)

/* cust.selectTCoCustList */ SELECT A.CUST_CODE , A.CUST_NAME , (SELECT ITEM_NAME FROM T_CO_ITEM X WHERE X.ITEM_CODE = A.CUST_TYPE1) AS CUST_TYPE1 , A.REGNUM , A.PRES_NAME , (SELECT USER_NAME FROM T_CO_CUST_USER X WHERE X.CUST_CODE = A.CUST_CODE AND X.USER_TYPE = '1' LIMIT 1) AS USER_NAME , DATE_FORMAT(A.CREATE_DATE, '%Y-%m-%d %H:%i') AS CREATE_DATE
ORDER BY A.CREATE_DATE DESC
LIMIT ${offset}, ${pageSize}

mapper.sql:
FROM T_CO_CUST_MASTER A
INNER JOIN T_CO_CUST_IR B
	ON A.CUST_CODE = B.CUST_CODE
WHERE	1=1

AND		B.INTERRELATED_CUST_CODE = #{interrelatedCustCode}



AND		A.CERT_YN = #{certYn}

AND		A.CERT_YN IN ('Y', 'D')



AND		A.CUST_NAME LIKE CONCAT('%', #{custName}, '%')


AND		A.CUST_TYPE1 = #{custTypeCode1}

mapper.insert:
/* cust.insertTCoCustMaster */
INSERT INTO T_CO_CUST_MASTER(
			CUST_TYPE1
,			CUST_TYPE2
,			CUST_NAME
,			REGNUM
,			PRES_NAME
,			PRES_JUMIN_NO
,			TEL
,			FAX
,			ZIPCODE
,			ADDR
,			ADDR_DETAIL
,			CAPITAL
,			FOUND_YEAR
,			CERT_YN
,			ETC
,			CREATE_USER
,			CREATE_DATE
,			UPDATE_USER
,			UPDATE_DATE
,			INTERRELATED_CUST_CODE
,			B_FILE
,			B_FILE_PATH
,			REGNUM_FILE
,			REGNUM_PATH
) VALUES (
			#{custType1}
,			#{custType2}
,			#{custName}
,			#{regnum}
,			#{presName}
,			#{presJuminNo}
,			#{tel}
,			#{fax}
,			#{zipcode}
,			#{addr}
,			#{addrDetail}
,			#{capital}
,			#{foundYear}
,			#{certYn}
,			#{etc}
,			#{userId}
,			NOW()
,			#{userId}
,			NOW()
,			#{interrelatedCustCode}
,			#{bFile}
,			#{bFilePath}
,			#{regnumFile}
,			#{regnumPath}
)

mapper.update:
/* cust.updateTCoCustMasterCert */
UPDATE T_CO_CUST_MASTER
SET		CERT_YN		= #{certYn}
,		ETC			= #{etc}
,		UPDATE_USER	= #{userId}
,		UPDATE_DATE	= NOW()
WHERE CUST_CODE		= #{custCode}

/* cust.updateTCoCustMaster */
UPDATE T_CO_CUST_MASTER
SET		CUST_NAME		= #{custName}
,		REGNUM			= #{regnum}
,		PRES_NAME		= #{presName}
,		PRES_JUMIN_NO	= #{presJuminNo}
,		TEL				= #{tel}
,		FAX				= #{fax}
,		ZIPCODE			= #{zipcode}
,		ADDR			= #{addr}
,		ADDR_DETAIL		= #{addrDetail}
,		CAPITAL			= #{capital}
,		FOUND_YEAR		= #{foundYear}
,		B_FILE			= #{bFile}
,		B_FILE_PATH		= #{bFilePath}
,		REGNUM_FILE		= #{regnumFile}
,		REGNUM_PATH		= #{regnumPath}
,		UPDATE_USER		= #{userId}
,		UPDATE_DATE		= NOW()
WHERE CUST_CODE			= #{custCode}

mapper.delete:
/* cust.deleteTCoCustMaster */
DELETE FROM T_CO_CUST_MASTER
WHERE CUST_CODE = #{custCode}

/* cust.deleteTCoCustIr */
DELETE FROM T_CO_CUST_IR
WHERE CUST_CODE = #{custCode}

/* cust.deleteTCoCustUser */
DELETE FROM T_CO_CUST_USER
WHERE CUST_CODE = #{custCode}

mapper.insert:
/* cust.mergeTCoCustIR */
INSERT INTO T_CO_CUST_IR (
		CUST_CODE
,		INTERRELATED_CUST_CODE
,		CUST_LEVEL
,		CUST_VALUATION
,		CARE_CONTENT
,		CERT_DATE
) VALUES (
		#{custCode}
,		#{interrelatedCustCode}
,		#{custLevel}
,		#{custValuation}
,		#{careContent}
,		NOW()
)
ON DUPLICATE KEY
UPDATE	CUST_LEVEL		= #{custLevel}
,		CUST_VALUATION	= #{custValuation}
,		CARE_CONTENT	= #{careContent}
,		CERT_DATE		= NOW()
