mapper.sql:
WHERE	1=1

	AND A.INTERRELATED_CUST_CODE = #{interrelatedCustCode}


	AND A.USE_YN = #{useYn}


	AND A.USER_NAME LIKE CONCAT('%',#{userName},'%')


	AND A.USER_ID LIKE CONCAT('%',#{userId},'%')

mapper.select:
		/* user.selectTCoCustUserList_count */
		SELECT	COUNT(1)
		FROM	T_CO_CUST_USER
		WHERE	CUST_CODE = #{custCode}
		
		AND		USER_NAME LIKE CONCAT('%', #{userName}, '%')
		
		
		AND		USER_ID LIKE CONCAT('%', #{userId}, '%')
		

		
		AND		USE_YN = #{useYn}

/* user.selectTCoCustUserList_count */ SELECT USER_NAME , USER_ID , USER_BUSEO , USER_POSITION , USER_EMAIL , USER_TEL , USER_HP , USER_TYPE , USE_YN FROM T_CO_CUST_USER WHERE CUST_CODE = #{custCode}
AND USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND USER_ID LIKE CONCAT('%', #{userId}, '%')
AND USE_YN = #{useYn}
ORDER BY CREATE_DATE
LIMIT ${offset}, ${pageSize}

mapper.insert:
/* user.insertTCoCustUser */
INSERT INTO T_CO_CUST_USER (
			USER_ID
,			CUST_CODE
,			USER_PWD
,			USER_NAME
,			USER_TEL
,			USER_HP
,			USER_EMAIL
,			USER_TYPE
,			USER_BUSEO
,			USER_POSITION
,			CREATE_USER
,			CREATE_DATE
,			PWD_CHG_DATE
,			USE_YN
) VALUES (
			#{userId}
,			#{custCode}
,			#{userPwd}
,			#{userName}
,			#{userTel}
,			#{userHp}
,			#{userEmail}
,			#{userType}
,			#{userBuseo}
,			#{userPosition}
,			#{updUserId}
,			NOW()
,			NOW()
,			#{useYn}
)

mapper.update:
/* user.updateTCoCustUser */
UPDATE T_CO_CUST_USER
SET		USER_NAME		= #{userName}
,		USER_TEL		= #{userTel}
,		USER_HP			= #{userHp}
,		USER_EMAIL		= #{userEmail}
,		USER_BUSEO		= #{userBuseo}
,		USER_POSITION	= #{userPosition}
,		UPDATE_USER		= #{updUserId}
,		UPDATE_DATE		= NOW()

,		USER_PWD		= #{userPwd}
,		PWD_CHG_DATE	= NOW()

WHERE	USER_ID			= #{userId}

/* user.updateTCoCustUserUseYn */
UPDATE T_CO_CUST_USER
SET	USE_YN		= #{useYn}
,	UPDATE_USER	= #{updUserId}
,	UPDATE_DATE	= NOW()
WHERE CUST_CODE	= #{custCode}

AND		USER_ID = #{delUserId}

mapper.select:
/* user.selectAdminUserList */
SELECT	USER_EMAIL
FROM	T_CO_USER
WHERE	USER_AUTH = #{userAuth}
AND		USE_YN = 'Y'
AND		INTERRELATED_CUST_CODE = #{interrelatedCustCode}

mapper.delete:
/* user.deleteTCoCustUser */
DELETE FROM T_CO_CUST_USER
WHERE CUST_CODE = #{custCode}
